import React, {useState} from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { setPopup } from '../../reducers/fileReducer'

function Popup() {
    const [fileName, setFileName] = useState('')
    const popupDislay = useSelector(state => state.files.popupDislay)
    const dispatch = useDispatch()

  return (
    <div className='popup' onClick={() => dispatch(setPopup)} style={{display: popupDislay}}>
        <div className="popup__content">
            <div className="popup__header">
                <div className="popup__title">Create a new folder</div>
                <button className="popup__close">X</button>
            </div>
            <input type="text" placeholder="Enter a new folder name" value={fileName} setValue={setFileName}/>
            <button className="popup__create">Create</button>
        </div>
    </div>
  )
}

export default Popup
