const SET_FILES = "SET_FILES"
const SET_CURRENT_DIRECTRY = "SET_CURRENT_DIRECTORY"
const ADD_FILE = "ADD_FILE"
const SET_POPUP = "SET_POPUP"

const defaultState = {
    files: [],
    currentDir: null,
    popupDisplay: 'none'
}

export default function fileReducer(state = defaultState, action) {
    switch (action.type) {
        case SET_FILES: return {...state, files: action.payload}
        case SET_CURRENT_DIRECTRY: return {...state, currentDir: action.payload}
        case ADD_FILE: return {...state, files: [...state.files, action.payload]}
        case SET_POPUP: return {...state, popupDisplay: action.payload}
        default:
            return state
    }
}

export const setFiles = (files) => ({type: SET_FILES, payload: files})
export const setDir = (dir) => ({type: SET_CURRENT_DIRECTRY, payload: dir})
export const addFile = (file) => ({type: ADD_FILE, payload: file})
export const setPopup = (display) => ({type: SET_POPUP, payload: display})
